select a.name,sum(r.item1), sum(r.item2), sum(r.item3), sum(r.item4), sum(r.item5), sum(r.item6), sum(r.item7), sum(r.item8), sum(r.item9), sum(r.item10), sum(r.item11), sum(r.item12), sum(r.item13), sum(r.item14), sum(r.item15), sum(r.item16), sum(r.item17), sum(r.item18), sum(r.item19), sum(r.item20), sum(r.item21), sum(r.item22), sum(r.item23), sum(r.item24), sum(r.item25), sum(r.item26), sum(r.item27), sum(r.item28), sum(r.item29), sum(r.item30), sum(r.item31), sum(r.item32), sum(r.item33), sum(r.item34), sum(r.item35), sum(r.item36), sum(r.item37), sum(r.item38), sum(r.item39), sum(r.item40), sum(r.item41), sum(r.item42), sum(r.item43), sum(r.item44), sum(r.item45), sum(r.item46) from fp_diqu c left outer join fp_diqu z on z.id=c.zhen_id left outer join fp_diqu a on a.id=z.area_id left outer join fp_dang_report r on r.cun_id=c.id and STR_TO_DATE(CONCAT(r.year,'-',r.time,'-01'),'%Y-%m-%d')>=STR_TO_DATE('2011-12-01','%Y-%m-%d') and STR_TO_DATE(CONCAT(r.year,'-',r.time,'-01'),'%Y-%m-%d')<=STR_TO_DATE('2011-12-01','%Y-%m-%d') where c.diqu_type='cun' and z.diqu_type='zhen' and a.diqu_type='area' group by a.name